    ativo: produto.ativo === true || produto.ativo === 'true' || produto.ativo === 1,
    bloqueado: produto.bloqueado === true || produto.bloqueado === 'true' || produto.bloqueado === 1,
    removido: produto.removido === true || produto.removido === 'true' || produto.removido === 1,
    tipo: produto.tipo || 'produto',

    // Pre√ßos
    preco_cheio: precoCorrespondente ? formatarPreco(precoCorrespondente.cheio) : 'R$ 0,00',
    preco_promocional: precoCorrespondente && precoCorrespondente.promocional ? 
                       formatarPreco(precoCorrespondente.promocional) : '',
