// Combinar dados de produtos e preços com SINCRONIZAÇÃO CORRIGIDA
// Lógica: usar preços como referência e sincronizar com Baserow
console.log('🔍 DEBUG - Iniciando sincronização com Baserow');

let produtos = [];
let precos = [];
let produtosComPreco = [];
let produtosExistentes = [];

try {
  // PRIMEIRO: Acessar dados da API de Preços (nosso ponto de referência)
  const dadosPrecos = $('Loja Integrada API - Preços').first().json.objects;
  if (dadosPrecos && dadosPrecos.length > 0) {
    console.log('💰 Preços encontrados:', dadosPrecos.length, 'itens');
    precos = dadosPrecos;

    // Extrair IDs dos produtos que têm preço
    const idsProdutosComPreco = precos
      .filter(preco => preco.cheio && parseFloat(preco.cheio) > 0)
      .map(preco => {
        const produtoUrl = preco.produto || '';
        const produtoId = produtoUrl.split('/').pop();
        return produtoId;
      })
      .filter(id => id); // Remove IDs vazios

    console.log('🎯 Produtos com preço válido encontrados:', idsProdutosComPreco.length);
    console.log('📋 IDs dos produtos com preço:', idsProdutosComPreco);

    // SEGUNDO: Acessar dados da API de Produtos e filtrar apenas os que têm preço
    const dadosProdutos = $('Loja Integrada API - Produtos').first().json.objects;
    if (dadosProdutos && dadosProdutos.length > 0) {
      console.log('📦 Produtos totais da API:', dadosProdutos.length, 'itens');

      // Filtrar apenas produtos que têm preço
      produtosComPreco = dadosProdutos.filter(produto => {
        const produtoTemPreco = idsProdutosComPreco.includes(produto.id?.toString());
        if (!produtoTemPreco) {
          console.log(`⚠️ Produto ${produto.nome} (ID: ${produto.id}) não tem preço - IGNORADO`);
        }
        return produtoTemPreco;
      });

      console.log('✅ Produtos filtrados com preço:', produtosComPreco.length);
      produtos = produtosComPreco;
    } else {
      console.log('⚠️ Nenhum produto encontrado na API de produtos');
    }
  } else {
    console.log('⚠️ Nenhum preço encontrado - não há produtos para processar');
  }

  // TERCEIRO: Verificar produtos existentes no Baserow para sincronização
  try {
    const baserowResponse = $('Baserow2').first().json;
    console.log('🔍 DEBUG - Resposta do Baserow2:', baserowResponse);

    // Verificar se a resposta tem a estrutura esperada
    if (baserowResponse && baserowResponse.results && Array.isArray(baserowResponse.results)) {
      produtosExistentes = baserowResponse.results;
      console.log('📊 Produtos existentes no Baserow (results):', produtosExistentes.length);
    } else if (baserowResponse && Array.isArray(baserowResponse)) {
      produtosExistentes = baserowResponse;
      console.log('📊 Produtos existentes no Baserow (formato direto):', produtosExistentes.length);
    } else {
      console.log('📊 Nenhum produto encontrado no Baserow - estrutura:', typeof baserowResponse);
      console.log('📊 Conteúdo:', baserowResponse);
    }
  } catch (error) {
    console.log('⚠️ Erro ao acessar produtos do Baserow:', error.message);
    console.log('📊 Considerando que não há produtos existentes');
  }

} catch (error) {
  console.log('❌ Erro ao acessar dados das APIs:', error.message);
}

console.log('✅ Produtos encontrados:', produtos.length);
console.log('✅ Preços encontrados:', precos.length);
console.log('📋 Lista de produtos:', produtos);
console.log('💵 Lista de preços:', precos);
console.log('🗄️ Produtos existentes no Baserow:', produtosExistentes.length);

// Função para limpar HTML
function limparHtml(html) {
  if (!html || typeof html !== 'string') {
    return '';
  }

  // Remove todas as tags HTML
  let textoLimpo = html.replace(/<[^>]*>/g, '');

  // Remove entidades HTML
  textoLimpo = textoLimpo.replace(/&[^;]+;/g, ' ');

  // Remove espaços extras e quebras de linha
  textoLimpo = textoLimpo.replace(/\s+/g, ' ').trim();

  return textoLimpo;
}

// Função para formatar preço
function formatarPreco(valor) {
  if (!valor) return 'R$ 0,00';

  const numero = parseFloat(valor);
  if (isNaN(numero)) return 'R$ 0,00';

  // Formatar como moeda brasileira
  return new Intl.NumberFormat('pt-BR', {
    style: 'currency',
    currency: 'BRL'
  }).format(numero);
}

// Função para encontrar preço do produto
function encontrarPreco(produtoId, listaPrecos) {
  console.log('🔍 Procurando preço para produto ID:', produtoId);
  console.log('📋 Lista de preços disponível:', listaPrecos?.length || 0);

  if (!produtoId || !listaPrecos || listaPrecos.length === 0) {
    console.log('❌ Parâmetros inválidos ou lista vazia');
    return null;
  }

  const produtoIdStr = produtoId.toString();
  console.log('🔄 Procurando por ID string:', produtoIdStr);

  const precoEncontrado = listaPrecos.find(preco => {
    // Verificar se o preço tem referência ao produto
    const precoProdutoUrl = preco.produto || '';
    console.log('Preço atual - URL produto:', precoProdutoUrl);

    const precoProdutoId = precoProdutoUrl.split('/').pop();
    console.log('Preço atual - ID extraído:', precoProdutoId);

    const match = precoProdutoId === produtoIdStr;
    console.log('Comparação:', precoProdutoId, '===', produtoIdStr, '=', match);

    return match;
  });

  console.log('Resultado da busca:', precoEncontrado ? 'Encontrado' : 'Não encontrado');
  return precoEncontrado;
}

// SINCRONIZAÇÃO: Comparar produtos da API com Baserow
console.log('🔄 Iniciando sincronização...');

// Criar mapa de produtos existentes no Baserow por ID
const mapaProdutosBaserow = new Map();
produtosExistentes.forEach(produto => {
  if (produto.id_produto_loja_integrada) {
    mapaProdutosBaserow.set(produto.id_produto_loja_integrada.toString(), produto);
    console.log(`🗂️ Mapeado produto existente: ID ${produto.id_produto_loja_integrada} -> Baserow ID ${produto.id}`);
  }
});

console.log('🗂️ Mapa de produtos Baserow criado com', mapaProdutosBaserow.size, 'itens');

// Preparar produtos para sincronização
const produtosParaSincronizar = [];
let produtosParaCreate = 0;
let produtosParaUpdate = 0;

// Processar cada produto da API
produtos.forEach((produto, index) => {
  console.log(`📦 Processando produto ${index + 1}/${produtos.length}:`, produto.nome, 'ID:', produto.id);

  // Encontrar preço correspondente
  const precoCorrespondente = encontrarPreco(produto.id, precos);
  console.log(`💰 Preço encontrado para ${produto.nome}:`, precoCorrespondente ? 'SIM' : 'NÃO');

  // Preparar dados do produto
  const produtoFinal = {
    // Dados do produto
    id_produto_loja_integrada: produto.id,
    nome: produto.nome,
    titulo_produto: produto.nome,
    apelido: produto.apelido,
    sku: produto.sku,
    descricao_completa: produto.descricao_completa,
    descricao_produto: limparHtml(produto.descricao_completa), // HTML limpo
    url_produto: produto.url || '',
    url_video_youtube: produto.url_video_youtube || '',
    gtin: produto.gtin || '',
    mpn: produto.mpn || '',
    ncm: produto.ncm || '',
    id_externo: produto.id_externo || '',
    ativo: produto.ativo,
    bloqueado: produto.bloqueado,
    removido: produto.removido,
    tipo: produto.tipo || 'produto',

    // Dados de preço (se encontrado)
    preco_cheio: precoCorrespondente ? formatarPreco(precoCorrespondente.cheio) : 'R$ 0,00',
    preco_promocional: precoCorrespondente && precoCorrespondente.promocional ? formatarPreco(precoCorrespondente.promocional) : null,
    preco_custo: precoCorrespondente && precoCorrespondente.custo ? formatarPreco(precoCorrespondente.custo) : null,
    sob_consulta: precoCorrespondente ? precoCorrespondente.sob_consulta : false,

    // Metadados
    data_sincronizacao: new Date().toISOString(),
    fonte: 'loja_integrada'
  };

  // Verificar se produto já existe no Baserow
  const produtoExistente = mapaProdutosBaserow.get(produto.id?.toString());
  console.log(`🔍 Verificando se produto ID ${produto.id} existe no Baserow:`, produtoExistente ? 'SIM' : 'NÃO');

  if (produtoExistente) {
    // Produto existe - precisa de UPDATE
    produtoFinal.acao = 'update';
    produtoFinal.id_baserow = produtoExistente.id; // ID do registro no Baserow
    produtosParaUpdate++;
    console.log(`🔄 Produto ${produto.nome} - UPDATE (ID Baserow: ${produtoExistente.id})`);
  } else {
    // Produto não existe - precisa de CREATE
    produtoFinal.acao = 'create';
    produtosParaCreate++;
    console.log(`➕ Produto ${produto.nome} - CREATE`);
  }

  produtosParaSincronizar.push(produtoFinal);
});

console.log('🎯 Sincronização preparada:');
console.log('- Produtos para CREATE:', produtosParaCreate);
console.log('- Produtos para UPDATE:', produtosParaUpdate);
console.log('- Total para sincronizar:', produtosParaSincronizar.length);

console.log('📊 Resumo geral:');
console.log('- Produtos da API com preço:', produtos.length);
console.log('- Preços encontrados:', precos.length);
console.log('- Produtos existentes no Baserow:', produtosExistentes.length);
console.log('- Produtos para CREATE:', produtosParaCreate);
console.log('- Produtos para UPDATE:', produtosParaUpdate);

console.log('📋 Primeiro produto para sincronizar:', produtosParaSincronizar[0]);

return produtosParaSincronizar;
