// Combinar dados de produtos e pre√ßos com SINCRONIZA√á√ÉO
// L√≥gica: usar pre√ßos como refer√™ncia e sincronizar com Baserow
console.log('üîç DEBUG - Iniciando sincroniza√ß√£o com Baserow');

let produtos = [];
let precos = [];
let produtosComPreco = [];
let produtosExistentes = [];

try {
  // PRIMEIRO: Acessar dados da API de Pre√ßos (nosso ponto de refer√™ncia)
  const dadosPrecos = $('Loja Integrada API - Pre√ßos').first().json.objects;
  if (dadosPrecos && dadosPrecos.length > 0) {
    console.log('üí∞ Pre√ßos encontrados:', dadosPrecos.length, 'itens');
    precos = dadosPrecos;

    // Extrair IDs dos produtos que t√™m pre√ßo
    const idsProdutosComPreco = precos
      .filter(preco => preco.cheio && parseFloat(preco.cheio) > 0)
      .map(preco => {
        const produtoUrl = preco.produto || '';
        const produtoId = produtoUrl.split('/').pop();
        return produtoId;
      })
      .filter(id => id); // Remove IDs vazios

    console.log('üéØ Produtos com pre√ßo v√°lido encontrados:', idsProdutosComPreco.length);
    console.log('üìã IDs dos produtos com pre√ßo:', idsProdutosComPreco);

    // SEGUNDO: Acessar dados da API de Produtos e filtrar apenas os que t√™m pre√ßo
    const dadosProdutos = $('Loja Integrada API - Produtos').first().json.objects;
    if (dadosProdutos && dadosProdutos.length > 0) {
      console.log('üì¶ Produtos totais da API:', dadosProdutos.length, 'itens');

      // Filtrar apenas produtos que t√™m pre√ßo
      produtosComPreco = dadosProdutos.filter(produto => {
        const produtoTemPreco = idsProdutosComPreco.includes(produto.id?.toString());
        if (!produtoTemPreco) {
          console.log(`‚ö†Ô∏è Produto ${produto.nome} (ID: ${produto.id}) n√£o tem pre√ßo - IGNORADO`);
        }
        return produtoTemPreco;
      });

      console.log('‚úÖ Produtos filtrados com pre√ßo:', produtosComPreco.length);
      produtos = produtosComPreco;
    } else {
      console.log('‚ö†Ô∏è Nenhum produto encontrado na API de produtos');
    }
  } else {
    console.log('‚ö†Ô∏è Nenhum pre√ßo encontrado - n√£o h√° produtos para processar');
  }

  // TERCEIRO: Verificar produtos existentes no Baserow para sincroniza√ß√£o
  try {
    const baserowProdutos = $('Baserow2').first().json;
    if (baserowProdutos && Array.isArray(baserowProdutos)) {
      produtosExistentes = baserowProdutos;
      console.log('üìä Produtos existentes no Baserow:', produtosExistentes.length);
    } else {
      console.log('üìä Nenhum produto encontrado no Baserow');
    }
  } catch (error) {
    console.log('‚ö†Ô∏è Erro ao acessar produtos do Baserow:', error.message);
    console.log('üìä Considerando que n√£o h√° produtos existentes');
  }

} catch (error) {
  console.log('‚ùå Erro ao acessar dados das APIs:', error.message);
}

console.log('‚úÖ Produtos encontrados:', produtos.length);
console.log('‚úÖ Pre√ßos encontrados:', precos.length);
console.log('üìã Lista de produtos:', produtos);
console.log('üíµ Lista de pre√ßos:', precos);
console.log('üóÑÔ∏è Produtos existentes no Baserow:', produtosExistentes.length);

// Fun√ß√£o para limpar HTML
function limparHtml(html) {
  if (!html || typeof html !== 'string') {
    return '';
  }

  // Remove todas as tags HTML
  let textoLimpo = html.replace(/<[^>]*>/g, '');

  // Remove entidades HTML
  textoLimpo = textoLimpo.replace(/&[^;]+;/g, ' ');

  // Remove espa√ßos extras e quebras de linha
  textoLimpo = textoLimpo.replace(/\s+/g, ' ').trim();

  return textoLimpo;
}

// Fun√ß√£o para formatar pre√ßo
function formatarPreco(valor) {
  if (!valor) return 'R$ 0,00';

  const numero = parseFloat(valor);
  if (isNaN(numero)) return 'R$ 0,00';

  // Formatar como moeda brasileira
  return new Intl.NumberFormat('pt-BR', {
    style: 'currency',
    currency: 'BRL'
  }).format(numero);
}

// Fun√ß√£o para encontrar pre√ßo do produto
function encontrarPreco(produtoId, listaPrecos) {
  console.log('üîç Procurando pre√ßo para produto ID:', produtoId);
  console.log('üìã Lista de pre√ßos dispon√≠vel:', listaPrecos?.length || 0);

  if (!produtoId || !listaPrecos || listaPrecos.length === 0) {
    console.log('‚ùå Par√¢metros inv√°lidos ou lista vazia');
    return null;
  }

  const produtoIdStr = produtoId.toString();
  console.log('üîÑ Procurando por ID string:', produtoIdStr);

  const precoEncontrado = listaPrecos.find(preco => {
    // Verificar se o pre√ßo tem refer√™ncia ao produto
    const precoProdutoUrl = preco.produto || '';
    console.log('Pre√ßo atual - URL produto:', precoProdutoUrl);

    const precoProdutoId = precoProdutoUrl.split('/').pop();
    console.log('Pre√ßo atual - ID extra√≠do:', precoProdutoId);

    const match = precoProdutoId === produtoIdStr;
    console.log('Compara√ß√£o:', precoProdutoId, '===', produtoIdStr, '=', match);

    return match;
  });

  console.log('Resultado da busca:', precoEncontrado ? 'Encontrado' : 'N√£o encontrado');
  return precoEncontrado;
}

// SINCRONIZA√á√ÉO: Comparar produtos da API com Baserow
console.log('üîÑ Iniciando sincroniza√ß√£o...');

// Criar mapa de produtos existentes no Baserow por ID
const mapaProdutosBaserow = new Map();
produtosExistentes.forEach(produto => {
  if (produto.id_produto_loja_integrada) {
    mapaProdutosBaserow.set(produto.id_produto_loja_integrada.toString(), produto);
  }
});

console.log('üóÇÔ∏è Mapa de produtos Baserow criado com', mapaProdutosBaserow.size, 'itens');

// Preparar produtos para sincroniza√ß√£o
const produtosParaSincronizar = [];
let produtosParaCreate = 0;
let produtosParaUpdate = 0;

// Processar cada produto da API
produtos.forEach((produto, index) => {
  console.log(`üì¶ Processando produto ${index + 1}/${produtos.length}:`, produto.nome, 'ID:', produto.id);

  // Encontrar pre√ßo correspondente
  const precoCorrespondente = encontrarPreco(produto.id, precos);
  console.log(`üí∞ Pre√ßo encontrado para ${produto.nome}:`, precoCorrespondente ? 'SIM' : 'N√ÉO');

  // Preparar dados do produto
  const produtoFinal = {
    // Dados do produto
    id_produto_loja_integrada: produto.id,
    nome: produto.nome,
    titulo_produto: produto.nome,
    apelido: produto.apelido,
    sku: produto.sku,
    descricao_completa: produto.descricao_completa,
    descricao_produto: limparHtml(produto.descricao_completa), // HTML limpo
    url_produto: produto.url || '',
    url_video_youtube: produto.url_video_youtube || '',
    gtin: produto.gtin || '',
    mpn: produto.mpn || '',
    ncm: produto.ncm || '',
    id_externo: produto.id_externo || '',
    ativo: produto.ativo,
    bloqueado: produto.bloqueado,
    removido: produto.removido,
    tipo: produto.tipo || 'produto',

    // Dados de pre√ßo (se encontrado)
    preco_cheio: precoCorrespondente ? formatarPreco(precoCorrespondente.cheio) : 'R$ 0,00',
    preco_promocional: precoCorrespondente && precoCorrespondente.promocional ? formatarPreco(precoCorrespondente.promocional) : null,
    preco_custo: precoCorrespondente && precoCorrespondente.custo ? formatarPreco(precoCorrespondente.custo) : null,
    sob_consulta: precoCorrespondente ? precoCorrespondente.sob_consulta : false,

    // Metadados
    data_sincronizacao: new Date().toISOString(),
    fonte: 'loja_integrada'
  };

  // Verificar se produto j√° existe no Baserow
  const produtoExistente = mapaProdutosBaserow.get(produto.id?.toString());

  if (produtoExistente) {
    // Produto existe - precisa de UPDATE
    produtoFinal.acao = 'update';
    produtoFinal.id_baserow = produtoExistente.id; // ID do registro no Baserow
    produtosParaUpdate++;
    console.log(`üîÑ Produto ${produto.nome} - UPDATE (ID Baserow: ${produtoExistente.id})`);
  } else {
    // Produto n√£o existe - precisa de CREATE
    produtoFinal.acao = 'create';
    produtosParaCreate++;
    console.log(`‚ûï Produto ${produto.nome} - CREATE`);
  }

  produtosParaSincronizar.push(produtoFinal);
});

console.log('üéØ Sincroniza√ß√£o preparada:');
console.log('- Produtos para CREATE:', produtosParaCreate);
console.log('- Produtos para UPDATE:', produtosParaUpdate);
console.log('- Total para sincronizar:', produtosParaSincronizar.length);

console.log('üìä Resumo geral:');
console.log('- Produtos da API com pre√ßo:', produtos.length);
console.log('- Pre√ßos encontrados:', precos.length);
console.log('- Produtos existentes no Baserow:', produtosExistentes.length);
console.log('- Produtos para CREATE:', produtosParaCreate);
console.log('- Produtos para UPDATE:', produtosParaUpdate);

console.log('üìã Primeiro produto para sincronizar:', produtosParaSincronizar[0]);

return produtosParaSincronizar;
